import React from "react";
import Showdown from 'showdown';

var text = document.getElementById("editor").ariaValueText;

function mdText(){
  let markdown = text;
  let html = convertMarkdownToHtml(markdown);
  return html;
}


const TextOutput = () => {
    return(
        <>
    <div class="converter">
<div class="toolbar">
<i class="fa fa-free-code-camp" title="logo"></i> Previewer
<i class="fa fa-arrows-alt"></i>
</div>
<div class="preview" onKeyUp={mdText}>
    
</div>
    </div>
    </>
    )
}

export default TextOutput;
