import React from "react";

const EditorWrap = () => {
return (
    <>
    <div class="wraptext">
        <div class="toolbar">
            <i class="fa fa-free-code-camp" title="logo"></i> Editor
            <i class="fa fa-arrows-alt"></i>
        </div>
        <textarea id="editor" type="text" value={postContent} onChange={e => setPostContent(e.target.value)}> </textarea>
    </div>
        
    </>
)
}


export default EditorWrap;
