import { useState } from 'react';
import './App.css';
import EditorWrap from './components/editortext';
import TextOutput from './components/textoutput';
import React from 'react';
import MarkdownPreview from './markdownpreview';



function App() {
  const [postContent, setPostContent] = useState('_Hello,_ **Markdown**!');
  return (
    <div className="App">
      <EditorWrap onchange={e=>setPostContent(e.target.value)}/>
      <TextOutput markdown={postContent}/>

    </div>
  );
}




export default App;
