import './App.css';
import React from 'react';
import { useState } from 'react';

const EditorWrap = () => {
  return (
      <>
      <div class="wraptext">
          <div class="toolbar">
              <i class="fa fa-free-code-camp" title="logo"></i> Editor
              <i class="fa fa-arrows-alt"></i>
          </div>
          <textarea id="editor" type="text" name="textarea"> </textarea>
      </div>

      </>
  )
  };

  const TextOutput = () => {
    return(
        <>
    <div class="converter">
<div class="toolbar">
<i class="fa fa-free-code-camp" title="logo"></i> Previewer
<i class="fa fa-arrows-alt"></i>
</div>
<div class="preview" id="input">
</div>
    </div>
    </>
    )
}

function App() {
    return (
    <div className="App">
      <EditorWrap />
      <TextOutput/>
         </div>
  );
}




export default App;
