import React from "react";
import Showdown from 'showdown';




function getText(){
    var data = document.getElementById("editor").ariaValueText;
    if(data.length > 0){
    document.getElementById("input").innerText = data;
    }
}

const TextOutput = () => {
    return(
        <>
    <div class="converter" onLoad={getText}>
<div class="toolbar">
<i class="fa fa-free-code-camp" title="logo"></i> Previewer
<i class="fa fa-arrows-alt"></i>
</div>
<div class="preview" id="input">
    
</div>
    </div>
    </>
    )
}

export default TextOutput;
